mapper.select:
/* Incoming.getDataList */ SELECT a.incomcd ,a.itemnm ,a.areasinm ,a.areasicd ,a.areagunm ,a.areagucd ,a.price ,a.memo ,a.regid ,a.regdt ,a.modid ,a.moddt ,a.useyn ,a.itgu ,a.mdopenyn ,ifnull(b.comnm, '') as comnm ,ifnull(b.comphone, '') as comphone ,if ( b.comphonelink != '', b.comphonelink, ifnull(b.comphone, '')) as comphonelink ,ifnull(b.usernm, '') as usernm FROM tb_etc_incoming a left join tb_member b on a.regid = b.userid WHERE 1 = 1
AND a.itemcd = #{itemcd}
AND a.areasicd = #{areasicd}
AND a.areagucd = #{areagucd}
AND a.regid = #{regid}
AND a.useyn = #{allyn}
AND a.itgu = #{itgu}
AND a.itemnm LIKE CONCAT('%', #{s_itemnm}, '%')
AND DATE_FORMAT(a.regdt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{stdt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{endt},''),'99991231'), '%Y%m%d')
ORDER BY a.regdt DESC, a.incomcd DESC, a.itemnm ASC
ORDER BY a.regdt DESC, a.incomcd DESC, a.itemnm ASC
ORDER BY a.itemnm ASC, a.regdt DESC, a.incomcd DESC
LIMIT #{offSet}, #{limit}

/* Incoming.getMyDataList */ SELECT A.incomcd ,A.itemnm ,A.areasinm ,A.areasicd ,A.areagunm ,A.areagucd ,A.price ,A.memo ,A.regid ,A.regdt ,A.modid ,A.moddt ,A.useyn ,A.itgu ,A.mdopenyn ,ifnull(C.comnm, '') as comnm ,ifnull(C.comphone, '') as comphone ,if ( C.comphonelink != '', C.comphonelink, ifnull(C.comphone, '')) as comphonelink ,ifnull(C.usernm, '') as usernm FROM tb_etc_incoming A inner join tb_my_item B on A.incomcd = B.incomcd and B.regid = #{regid} left join tb_member C on A.regid = C.userid WHERE 1 = 1
AND A.itemcd = #{itemcd}
AND A.yearcd = #{yearcd}
AND A.areasicd = #{areasicd}
AND A.areagucd = #{areagucd}
AND A.useyn = #{allyn}
AND A.itgu = #{itgu}
AND A.itemnm LIKE CONCAT('%', #{s_itemnm}, '%')
AND DATE_FORMAT(A.regdt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{A.stdt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{A.endt},''),'99991231'), '%Y%m%d')
ORDER BY A.regdt DESC, A.incomcd DESC, A.itemnm ASC
ORDER BY A.regdt DESC, A.incomcd DESC, A.itemnm ASC
ORDER BY A.itemnm ASC, A.regdt DESC, A.incomcd DESC
LIMIT #{offSet}, #{limit}

/* Incoming.getClickDataList */ SELECT A.incomcd ,A.itemnm ,A.areasinm ,A.areasicd ,A.areagunm ,A.areagucd ,A.price ,A.memo ,A.regid ,A.regdt ,A.modid ,A.moddt ,A.useyn ,A.itgu ,A.mdopenyn ,ifnull(C.comnm, '') as comnm ,ifnull(C.comphone, '') as comphone ,ifnull(C.comphonelink, '') as comphonelink ,if ( C.comphonelink != '', C.comphonelink, ifnull(C.comphone, '')) as comphonelink ,ifnull(C.usernm, '') as usernm FROM tb_etc_incoming A inner join tb_click_item B on A.incomcd = B.incomcd and B.regid = #{regid} left join tb_member C on A.regid = C.userid WHERE 1 = 1
AND A.itemcd = #{itemcd}
AND A.areasicd = #{areasicd}
AND A.areagucd = #{areagucd}
AND A.useyn = #{allyn}
AND A.itgu = #{itgu}
AND A.itemnm LIKE CONCAT('%', #{s_itemnm}, '%')
AND DATE_FORMAT(A.regdt, '%Y%m%d') BETWEEN DATE_FORMAT(IFNULL(NULLIF(#{A.stdt},''),'00000000'), '%Y%m%d') AND DATE_FORMAT(IFNULL(NULLIF(#{A.endt},''),'99991231'), '%Y%m%d')
ORDER BY B.regdt DESC, A.incomcd DESC, A.itemnm ASC
ORDER BY B.regdt DESC, A.incomcd DESC, A.itemnm ASC
ORDER BY A.itemnm ASC, B.regdt DESC, A.incomcd DESC
LIMIT #{offSet}, #{limit}

mapper.insert:

/* Incoming.insData */
INSERT INTO tb_etc_incoming
(
     incomcd
    ,itemnm
    ,areasinm
    ,areasicd
    ,areagunm
    ,areagucd
    ,price
    ,memo
    ,itgu
    ,regid
    ,regdt
    ,modid
    ,moddt
)
VALUES
    (
     #{incomcd}
    ,#{itemnm}
    ,#{areasinm}
    ,#{areasicd}
    ,#{areagunm}
    ,#{areagucd}
    ,#{price}
    ,#{memo}
    ,#{itgu}
    ,#{regid}
    ,now()
    ,#{modid}
    ,now()
    )
    ON DUPLICATE KEY
UPDATE
    itemnm = #{itemnm}
    ,areasinm = #{areasinm}
    ,areasicd = #{areasicd}
    ,areagunm = #{areagunm}
    ,areagucd = #{areagucd}
    ,price = #{price}
    ,memo = #{memo}
    ,itgu = #{itgu}
    ,modid = #{modid}
    ,moddt = now()

mapper.delete:

/* Incoming.delData */
DELETE FROM tb_etc_incoming
WHERE incomcd = #{incomcd}

mapper.update:

/* Incoming.dataViewOpt */
UPDATE tb_etc_incoming
SET useyn = #{useyn}
WHERE incomcd = #{incomcd}
